import React, { useEffect, useRef, useState } from 'react'
import axios from 'axios'
import useInput from '../../hooks/useInput'
import { useHistory } from "react-router-dom";
import Cookies from 'js-cookie'
const rest = require('../../BackEndApis')
function AddUser() {
    const messageRef = useRef()
    const history = useHistory();
    const [name, setName,  bindName, resetName] = useInput('')
    const [email, setEmail,  bindEmail, resetEmail] = useInput('')
    const [address, setAddress,  bindAddress, resetAddress] = useInput('')
    const [phone, setPhone,  bindPhone, resetPhone] = useInput('')
    const [password, setPassword, bindPassword, resetPassword] = useInput('')
    const [userType, setUserType, bindUserType, resetUserType] = useInput('')
    const header = {
        headers: {
            "Content-type": "Application/json",
            "Authorization": `Bearer ${Cookies.get('token')}`
        }
    }
    const submitHandler = (e) => {
        e.preventDefault()
        if(name === '' || email==='' || phone==='' || password==='' || userType==='' ||  address === '') {
            messageRef.current.innerHTML = "<span>* Please Fill All Fields</span>"
        }  else {
            let data = {
                "name":name,
                "email":email,
                "phone":phone,
                "password":password,
                "userType":userType,
                "address":address,
            }
            axios.post(rest.endPointUsers,data,header)
            .then(response => {
                console.log(response.data);
                alert(response.data)
                setName('')
                setEmail('')
                setAddress('')
                setPhone('')
                setPassword('')
                setUserType('')
            })
            .catch((err) => {
                console.log(err.response.data.error);
                messageRef.current.innerHTML = "<span>* Invalid Login Details</span>"
            })
        }
    }
    return (
        <div className='my-background'>
            <div className='row'>
                <div className='col-md-4'></div>
                <div className='col-md-4'>
                    <div className='card p-3 mt-2'>
                        <form onSubmit={submitHandler}>  
                            <div className='text-center mytitle2'>Add New User</div>
                            <div className='mt-2'>
                                <label className='form-control-lable'>Name</label>
                                <input type='text' {...bindName}  className='form-control' placeholder='Enter Name'  required/>
                            </div>
                            <div className='mt-2'>
                                <label className='form-control-lable'>Email</label>
                                <input type='email' {...bindEmail}  className='form-control' placeholder='Enter Email'  required/>
                            </div>
                            <div className='mt-2'>
                                <label className='form-control-lable'>Phone</label>
                                <input type='number' {...bindPhone}  className='form-control' placeholder='Enter Phone Number'  required/>
                            </div>
                            <div className='mt-2'>
                                <label className='form-control-lable'>Password</label>
                                <input type='password' {...bindPassword}  className='form-control' placeholder='Enter Password'  required/>
                            </div>
                            <div className='mt-2'>
                                <label className='form-control-lable'>CHoose User Type</label>
                                <select required className='form-control' {...bindUserType} >
                                    <option value=''>Choose User Type</option>
                                    <option value='StockManager'>Stock Manager</option>
                                    <option value='SalesPerson'>Sales Person</option>
                                    <option value='CustomerRepresentative'>Customer Representative</option>
                                </select>
                            </div>
                            <div className='mt-2'>
                                <label className='form-control-lable'>Address</label>
                                <textarea {...bindAddress}  className='form-control' placeholder='Enter Address'  required></textarea>
                            </div>
                            <div className='mt-3'>
                                <input type='submit' value='Add User' className='btn w-100' style={{backgroundColor:'#34495e',color:'white'}}/>
                            </div>

                        </form>
                    </div>
                    <div>
                        <div ref={messageRef} className="m-5 text-end" style={{color:'white'}}></div>
                    </div>
                </div>
                <div className='col-md-4'></div>
            </div>
        </div>
    )
}
export default AddUser
